mapper.select:
SELECT
    *
from
    with_view

    `close` = 0 and del = 0 and deadline >= CURDATE()

       and category_id = #{categoryId}


        and (title like '%${query}%' or content like '%${query}%')


        and position_id = #{positionId}


        and way_id = #{wayId}


        and member_id = #{memberId}


        and tech_id in

            #{item}





group by id
order by id desc
limit #{offset}, #{size}

select
    count(*)
from
(SELECT
*
from
with_view

    `close` = 0 and del = 0 and deadline >= CURDATE()

        and category_id = #{categoryId}


        and (title like '%${query}%' or content like '%${query}%')


        and position_id = #{positionId}


        and way_id = #{wayId}


        and member_id = #{memberId}


        and tech_id in

            #{item}





group by id) w
